@njit(nogil=NOGIL, cache=CACHE)
def cesforecast(states, n, m, season, f, h, alpha_0, alpha_1, beta_0, beta_1):
    # compute forecasts
    m = 1 if season == NONE else m
    new_states = cesfcst(
        states=states,
        i=m + n,
        m=m,
        season=season,
        f=f,
        h=h,
        alpha_0=alpha_0,
        alpha_1=alpha_1,
        beta_0=beta_0,
        beta_1=beta_1,
    )
    return new_states

