def pegelsfcast_C(h, obj, npaths=None, level=None, bootstrap=None):
    forecast = np.full(h, fill_value=np.nan)
    m = obj["m"]
    n = obj["n"]
    states = obj["states"]
    cesforecast(
        states=states,
        n=n,
        m=m,
        season=switch_ces(obj["seasontype"]),
        h=h,
        f=forecast,
        **obj["par"]
    )
    return forecast

